import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

class App extends Component {
state = {
    data: null
  };

  componentDidMount() {
    this.callBackendAPI()
      .then(res => this.setState({ data: res.express }))
      .catch(err => console.log(err));
  }
    // fetching the GET route from the Express server which matches the GET route from server.js
  callBackendAPI = async () => {
    const response = await fetch('/express_backend');
    const body = await response.json();

    if (response.status !== 200) {
      throw Error(body.message) 
    }
    return body;
  };

  render() {
    return (
      <div class="container" id="site-content">
        <div class="row" id="confession-form-row">
          <form id="confession-form" method="post" action="/">
              <span class="form-group">
                  <div class="form-check form-check-inline">
                      <input class="form-check-input" type="radio" name="uniRadios" id="uomRadio" value="UoM"/>
                      <label class="form-check-label" for="uomRadio">
                      UoM
                      </label>
                  </div>
                  <div class="form-check form-check-inline">
                      <input class="form-check-input" type="radio" name="uniRadios" id="mmuRadio" value="MMU"/>
                      <label class="form-check-label" for="mmuRadio">
                      MMU
                      </label>
                  </div>
                  <div class="form-check form-check-inline">
                      <input class="form-check-input" type="radio" name="uniRadios" id="uosRadio" value="UoS"/>
                      <label class="form-check-label" for="uosRadio">
                      UoS
                      </label>
                  </div>
                  <div class="form-check form-check-inline">
                      <input class="form-check-input" type="radio" name="uniRadios" id="otherRadio" value="Other"/>
                      <label class="form-check-label" for="otherRadio">
                      Other
                      </label>
                  </div>
              </span>
              <div class="form-group">
                  <label for="confessionTextArea">Confession</label>
                  <textarea class="form-control" id="confessionTextArea" rows="5" maxlength="1024" name="content"></textarea>
                  <small id="confessionAnonymous" class="form-text text-muted">All confessions are completely anonymous</small>
              </div>
              <button type="submit" class="btn btn-danger">Confess</button>
            </form>
          </div>
      </div>
    );
  }
}

export default App;
