import React from 'react';
import './App.css';

export default class ConfessionForm extends React.Component {
    constructor(props){
        super(props);

    this.state = {uniRadios: 'Other', content: ''};
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event){
        const value = event.target.value;
        this.setState({
            ...this.state,
            [event.target.name]: value
        });
    }
    handleSubmit(event){
      alert('A value was submitted ' + this.state.uniRadios);
      fetch('/submit_content', {
        method: 'POST',
        body: JSON.stringify(this.state),
        headers: {
            'Content-Type': 'application/json'
        }
      }).then(function(response) {
        console.log(response);
        return response;
      })

      event.preventDefault();
    }

    render(){
        return (
            <div className="row" id="confession-form-row">
            <form id="confession-form" onSubmit={this.handleSubmit}>
                <span className="form-group">
                    <div className="form-check form-check-inline">
                        <input
                            className="form-check-input"
                            type="radio"
                            name="uniRadios"
                            id="uomRadio"
                            value="UoM"
                            checked={this.state.uniRadios === "UoM"}
                            onChange={this.handleChange}
                        />
                        <label className="form-check-label" htmlFor="uomRadio">
                        UoM
                        </label>
                    </div>
                    <div className="form-check form-check-inline">
                        <input
                            className="form-check-input"
                            type="radio"
                            name="uniRadios"
                            id="mmuRadio"
                            value="MMU"
                            checked={this.state.uniRadios === "MMU"}
                            onChange={this.handleChange}
                        />
                        <label className="form-check-label" htmlFor="uomRadio">
                        MMU
                        </label>
                    </div>
                    <div className="form-check form-check-inline">
                        <input
                            className="form-check-input"
                            type="radio"
                            name="uniRadios"
                            id="uosRadio"
                            value="UoS"
                            checked={this.state.uniRadios === "UoS"}
                            onChange={this.handleChange}
                        />
                        <label className="form-check-label" htmlFor="uomRadio">
                        UoS
                        </label>
                    </div>
                    <div className="form-check form-check-inline">
                        <input
                            className="form-check-input"
                            type="radio"
                            name="uniRadios"
                            id="otherRadio"
                            value="Other"
                            checked={this.state.uniRadios === "Other"}
                            onChange={this.handleChange}
                        />
                        <label className="form-check-label" htmlFor="uomRadio">
                        Other
                        </label>
                    </div>
                </span>
                <div className="form-group">
                    <label htmlFor="confessionTextArea">Confession</label>
                    <textarea 
                        className="form-control"
                        id="confessionTextArea"
                        rows="5"
                        maxLength="1024"
                        name="content"
                        onChange={this.handleChange}
                    ></textarea>
                    <small id="confessionAnonymous" className="form-text text-muted">All confessions are completely anonymous</small>
                </div>
                <button type="submit" className="btn btn-danger">Confess</button>
            </form>
            </div>
        )
    }
}
